import './App.css';
import React from 'react';
import axios from 'axios';
import { Table, Tag, Space } from 'antd';


const columns = [
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
    render: text => <a>{text}</a>,
  },
  {
    title: 'Age',
    dataIndex: 'age',
    key: 'age',
  },
  {
    title: 'Address',
    dataIndex: 'address',
    key: 'address',
  },
  {
    title: 'Tags',
    key: 'tags',
    dataIndex: 'tags',
    render: tags => (
      <>
        {tags.map(tag => {
          let color = tag.length > 5 ? 'geekblue' : 'green';
          if (tag === 'loser') {
            color = 'volcano';
          }
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </>
    ),
  },
  {
    title: 'Action',
    key: 'action',
    render: (text, record) => (
      <Space size="middle">
        <a>Invite {record.name}</a>
        <a>Delete</a>
      </Space>
    ),
  },
];

class App extends React.Component {




   componentDidMount()
   {


   }


  constructor(props) {
    super(props);
    this.state = {name: '',desc:'',type:'',fuelType:'',manu:''};

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
  }



  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(event) {
    debugger;
    event.preventDefault();

    const cardata = { 
      name:this.state.name,
      desc:this.state.desc,
      typefuel:this.state.fuelType,
      type:this.state.type,
      manu:this.state.manu,
     };

     debugger;


    axios.put('http://localhost:3000/cars', cardata)
    .then(res => {
      debugger;
      if(res.status===202)
      {
       alert("Car added to db");
       this.setState({
        name: '',desc:'',type:'',fuelType:'',manu:''
      });
      }

    })



  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1>Add Car</h1>
        <form onSubmit={this.handleSubmit}>
    <label>
      Name:
      <input type="text" name="name" value={this.state.name} onChange={this.handleInputChange} />
    </label>
    <br></br>
    <label>
      Desc:
      <input type="text" name="desc" value={this.state.desc} onChange={this.handleInputChange}  />
    </label>
    <br></br>
    <label>
      Type:
      <input type="text" name="type" value={this.state.type} onChange={this.handleInputChange} />
    </label>
    <br></br>
    <label>
      fuelType:
      <input type="text" name="fuelType" value={this.state.fuelType} onChange={this.handleInputChange} />
    </label>
    <br></br>
    <label>
    manufacturer:
      <input type="text" name="manu"  value={this.state.manu} onChange={this.handleInputChange}/>
    </label>
    <input type="submit" value="Submit" />
  </form>

  <Table columns={columns} dataSource={data}></Table>
        </header>
      </div>
    );


  }

}

export default App;
